import React from 'react';
import image from '../../assets/img/core-img/paypal.png';
import CartPage from "../../pages/cart";
import * as PropTypes from "prop-types";

CartPage.propTypes = {
    cart: PropTypes.array,
    catalog: PropTypes.array,
    catalogLoading: PropTypes.bool,
    uploadDataToStore: PropTypes.func,
    onShopPage: PropTypes.bool,
    sum: PropTypes.number,
    updateCart: PropTypes.func,
    updateOnPageShopFilter: PropTypes.func,
    updateSum: PropTypes.func,
};

function CheckoutCartSummary(props) {
    const {
        cart,
        catalog,
        catalogLoading,
        uploadDataToStore,
        onShopPage,
        sum,
        updateCart,
        updateOnPageShopFilter,
        updateSum,
    } = props;
    let newSum = sum;

    SumMeter();

    function SumMeter() { // вычисляем сумму всех покупок
        newSum = 0;
        for (let i = 0; i < cart.length; i++) {
            for (let j = 0; j < catalog.length; j++) {
                if (cart[i].item === catalog[j].id) {
                    let curSum = catalog[j].price * cart[i].amount;
                    // newSum = + newSum + curSum;
                    newSum = newSum + curSum;
                };
            };
        };
        updateSum(newSum); // обновляем сумму в state
    };

    return (
        <div className="col-12 col-lg-4">
            <div className="cart-summary">

                <h5>Cart Total</h5>

                <ul className="summary-table">
                    <li><span>subtotal:</span> <span>${newSum}</span></li>
                    <li><span>delivery:</span> <span>Free</span></li>
                    <li><span>total:</span> <span>${this.props.sum}</span></li>
                </ul>

                <div className="payment-method">

                    <div className="custom-control custom-checkbox mr-sm-2">
                        <input type="checkbox" className="custom-control-input" id="cod" checked />
                        <label className="custom-control-label" htmlFor="cod">Cash on Delivery</label>
                    </div>

                    <div className="custom-control custom-checkbox mr-sm-2">
                        <input type="checkbox" className="custom-control-input" id="paypal" />
                        <label className="custom-control-label" htmlFor="paypal">Paypal <img className="ml-15" src={image} alt="" /></label>
                    </div>

                </div>

                <div className="cart-btn mt-100">
                    <button className="btn amado-btn w-100">Checkout</button>
                </div>

            </div>
        </div>
    );
}

export default React.memo(CheckoutCartSummary);
