import React from 'react';
import { connect } from 'react-redux';
import Spinner from "../components/spinner";
import { Link } from 'react-router-dom';
import * as URL from '../router/url';
import * as PropTypes from 'prop-types';
import { uploadDataToStore, activeCarouselChanger, updateCart, updateSum, itemMemoryChecker } from '../store/action_creatores';
import Demo from '../components/Ranking/rating';
import Feedback from "../components/Ranking/feedback";


Product.propTypes = {
    catalog: PropTypes.array,
    categories: PropTypes.array,
    match: PropTypes.object,
    catalogLoading: PropTypes.bool,
    imageItemLoading: PropTypes.bool,
    revievItemLoading: PropTypes.bool,
    categoriesLoading: PropTypes.bool,
    singleImageItem: PropTypes.array,
    singleRevievItem: PropTypes.array,
    uploadDataToStore: PropTypes.func,
    activeCarouselChanger: PropTypes.func,
    activeImg: PropTypes.number,
    itemMemory: PropTypes.string,
    itemMemoryChecker: PropTypes.func,
    cart: PropTypes.array,
    sum: PropTypes.number,
    updateCart: PropTypes.func,
    updateSum: PropTypes.func,
};

function Product(props) { // –≤–æ–∑–≤—Ä–∞—â–∞–µ–º –æ–ø–∏—Å–∞–Ω–∏–µ –û–î–ò–ù–û–ß–ù–û–ì–û —Ç–æ–≤–∞—Ä–∞ –ø—Ä–∏ –∫–ª–∏–∫–µ –Ω–∞ –∫–∞—Ä—Ç–∏–Ω–∫—É —Ç–æ–≤–∞—Ä–∞ –≤ shop

    const {
        catalog,
        categories,
        match,
        catalogLoading,
        imageItemLoading,
        revievItemLoading,
        categoriesLoading,
        singleImageItem,
        singleRevievItem,
        uploadDataToStore,
        activeCarouselChanger,
        activeImg,
        itemMemory,
        itemMemoryChecker,
        cart,
        sum,
        updateCart,
        updateSum,
    } = props;

    let item = {};
    let catName; // –¥–ª—è —Ö–ª–µ–±–Ω—ã—Ö –∫—Ä–æ—à–µ–∫
    let cartItemBayStatus = "ADD TO CART"; // –Ω–∞–¥–ø–∏—Å—å –Ω–∞ –∫–Ω–æ–ø–∫–µ

    if (singleImageItem === undefined || itemMemory !== match.params.code) {
        itemMemoryChecker(match.params.code)
        uploadDataToStore("imageItem", `?cid=${match.params.code}`);
    };

    if (singleRevievItem === undefined || itemMemory !== match.params.code) {
        itemMemoryChecker(match.params.code)
        uploadDataToStore("revievItem", `?cid=${match.params.code}`);
    };

    if (imageItemLoading || catalogLoading || revievItemLoading || categoriesLoading) {
        return (<Spinner />);
    };

    if (!catalog.find(elem => elem.id === match.params.code)) {
        return (
            <h6 style={{
                margin: "50px",
                padding: "5px",
                color: "red",
                border: "solid red 2px",
                borderRadius: "5px",
                height: "fit-content",
            }}>Error! <br />
            The product is missing! <br />
            </h6>
        );
    };

    if (catalog && catalog.length) {
        for (let i = 0; i < catalog.length; i++) {
            if (catalog[i].id === match.params.code) {
                item = catalog[i];
                let crutch = catalog[i].category; // –≤–æ –∏–∑–±–µ–∂–∞–Ω–∏–µ "Function declared in a loop contains unsafe references to variable..."
                let cat = categories.find(element => element.id === crutch);
                catName = cat.title;
            };
        };
    };

    function carouselleItemRender() { // –≥–µ–Ω–µ—Ä–∏—Ä—É–µ–º –∫–∞—Ä—É—Å–µ–ª—å (—ç–∫—Å–∫–ª—é–∑–∏–≤–Ω–æ –¥–ª—è —Å—Ç—É–ª–∞ "–ì—Ä—É—Å—Ç–Ω—ã–π")
