import React, { Component } from "react";

export default class ScrollToTop extends Component {
    constructor(props) {
        super(props);
        this.state = {
            is_visible: false
        };
    }

    componentDidMount() {
        let scrollComponent = this;
        document.addEventListener("scroll", function(e) {
            scrollComponent.toggleVisibility();
        });
    }

    toggleVisibility() {
        if (window.pageYOffset > 300) {
            this.setState({
                is_visible: true
            });
        } else {
            this.setState({
                is_visible: false
            });
        }
    }

    scrollToTop() {
        window.scrollTo({
            top: 0,
            behavior: "smooth"
        });
    }

    render() {
        const { is_visible } = this.state;
        return (
            <div className="scroll-to-top">
                {is_visible && (
                    <div onClick={() => this.scrollToTop()}>
                        <svg
                            xmlns="http://www.w3.org/2000/svg"
                            height="38.735"
                            width="33.749"
                        >
                            <g transform="translate(-18.121 -3.364)">
                                <rect
                                    ry="4.928"
                                    y="3.364"
                                    x="18.121"
                                    height="38.735"
                                    width="33.749"
                                    fill="#00f"
                                />
                                <g transform="translate(-.48 2.134)">
                                    <rect
                                        ry="4.928"
                                        y="1.229"
                                        x="18.601"
                                        height="38.735"
                                        width="33.749"
                                        fill="url(#b)"
                                    />
                                    <g fill="#ececec">
                                        <path d="M22.435 17.62l4.684 4.685 5.044-5.044v19.352h6.625V17.26l5.044 5.044 4.683-4.684-13.04-13.04z" />
                                        <path d="M22.435 17.62l4.684 4.685 5.044-5.044v19.352h6.625V17.26l5.044 5.044 4.683-4.684-13.04-13.04z" />
                                    </g>
                                </g>
                            </g>
                        </svg>
                    </div>
                )}
            </div>
        );
    }
}


<a id="scrollUp" href="#top" style="position: fixed; z-index: 2147483647; display: block;"><i className="fa fa-angle-up"
                                                                                              aria-hidden="true"></i></a>
